mapper.select:
SELECT * FROM ( SELECT a.parent_code AS parent_code, a.id AS folder_id, '' AS file_upload_id, a.folder_name AS file_name, 'folder' AS file_type, a.tree_sort AS tree_sort, a.remarks AS remarks, a.create_by AS create_by, a.create_date AS create_date, a.update_by AS update_by, a.update_date AS update_date, null AS file_extension, null AS file_size, cu.user_name AS create_by_name FROM js_filemanager_folder a JOIN js_sys_user cu ON cu.user_code = a.create_by
${sqlMap.where.toSql()}
AND a.corp_code = #{corpCode}
AND a.group_type = #{groupType}
AND a.parent_code = #{folderId}
AND a.parent_code = '0'
AND a.folder_name LIKE #{fileNameLike}
UNION ALL SELECT null AS parent_code, '' AS folder_id, a.id AS file_upload_id, a.file_name AS file_name, a.file_type AS file_type, 9999999999 AS tree_sort, a.remarks AS remarks, a.create_by AS create_by, a.create_date AS create_date, a.update_by AS update_by, a.update_date AS update_date, e.file_extension AS file_extension, e.file_size AS file_size, cu.user_name AS create_by_name FROM js_sys_file_upload a JOIN js_sys_file_entity e ON e.file_id = a.file_id JOIN js_sys_user cu ON cu.user_code = a.create_by
${sqlMap.where.toSql()}
AND a.biz_type = #{bizType}
AND a.biz_key = #{folderId}
AND a.biz_key = '0'
AND a.file_name LIKE #{fileNameLike}
) a ORDER BY ${sqlMap.order.toSql()}

